@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans+Thai:wght@400;500&display=swap');

:root {
  --red-color: #ff4b4b;
  --dark-red-color: #ff7878;
  --dark-blue-color: #007bff;
  --blue-color: #64A2FF;
  --light-blue-color-border: #afcfff;
  --light-blue-color: #e3eeff;
  --black-color: #000000;
  --white-color: #fff;
  --dark-grey-color: #777777;
  --grey-color: #B2B2B2;
  --light-grey-color: #eee;
  --grey-input: #eee;
  --grey-background: #F5F5F5;
}

* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

html {
  font-size: 16px;
  background-color: var(--grey-background);
}

body {
  font-size: 16px;
  font-family: 'IBM Plex Sans Thai';
  color: var(--black-color);
}
p {
  font-family: 'Roboto', sans-serif;
}
.container {
  display: flex;
}

.main-content {
  flex-grow: 1;
  /* Make this section take up the remaining space */
  overflow-y: auto;
  background-color: #f4f4f4;
  /* Light background to contrast with sidebar */
}

.middle-box {
  width: 75%;
  height: 100vh;
  background-color: var(--white-color);
  padding: 20px;
  border-radius: 8px;
  overflow-y: auto;
}

.right-box {
  background-color: var(--white-color);
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  border-radius: 8px;
  padding: 20px;
  height: 100vh;
  width: 43vh;
  margin-left: 20px;
  position: relative;
  overflow-y: auto;
}

.right-box img {
  width: 100%;
  height: 160px;
  object-fit: cover;
  border-radius: 5px;
  margin: 18px 0 18px 0;
}

.right-box-header {
  border-bottom: 1px solid #ddd;
  display: flex;
  flex-direction: column;
}

/* resets */
ul {
  list-style-type: none;
}

a {
  text-decoration: none;
  color: var(--black-color);
}

img {
  width: 100%;
  display: block;
}

/* Inout Styles */
input {
  font-family: inherit;
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  width: 100%;
  font-size: 16px;
  transition: border-color 0.3s ease;
}
  
input:focus {
  border-color: var(--blue-color);
  outline: none;
}

/* Dropdown Styles */
select {
  font-family: inherit;
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  width: 100%;
  font-size: 16px;
  transition: border-color 0.3s ease;
}
    
select:focus {
  border-color: var(--blue-color);
  outline: none;
}
    
select option {
  padding: 10px;
}

/* กล่องตัวเลือกที่อยู่บรรทัดเดียวกัน */

.option-box {
  font-family: inherit;
  font-size: 15px;
  background-color: var(--white-color);
  border: 1px solid #ddd;
  padding: 8px 13px;
  border-radius: 20px;
  margin: 0 10px 0 0;
  cursor: pointer;
  display: inline-block;
  transition: background-color 0.3s, border-color 0.3s;
}

.option-box input[type="radio"] {
  display: none;
}

.option-box:hover {
  background-color: var(--light-blue-color-border);
}

.option-box.selected {
  background-color: var(--blue-color);
  border-color: var(--blue-color);
  color: white;
}

/* ทำให้อยู่บรรทัดเดียวกัน */
.option-list {
  list-style-type: none;
  padding: 0;
  margin: 0;
}

.option-item {
  padding: 6px;
  border-bottom: 1px solid #ddd;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.option-item-column {
  padding: 6px;
  border-bottom: 1px solid #ddd;
  display: flex;
  flex-direction: column; /* ทำให้ label กับ input เป็นคนละบรรทัด */
}

.option-item:last-child {
  border-bottom: none;
}
.option-item-column:last-child {
  border-bottom: none;
}

.inline-elements {
  display: flex;
  align-items: center; /* Align items vertically in the center */
  gap: 10px; /* Add some space between the elements */
}

.inline-elements select,
.inline-elements input {
  flex: 1; /* Allow the elements to resize proportionally if needed */
  max-width: 200px; /* Optional: Set a max width for consistency */
}

/* backgrounds */
.bg-white {
  background-color: var(--white-color);
}

.bg-light-grey {
  background-color: var(--grey-background);
}

/* text */
.text-center {
  text-align: center;
}

.text-right {
  text-align: right;
}

.text-left {
  text-align: left;
}

.text-black {
  color: var(--black-color);
}

.text-blue {
  color: var(--dark-blue-color);
}

.text-red {
  color: var(--red-color);
}

.text-dark-grey {
  color: var(--dark-grey-color);
}

.text-white {
  color: var(--white-color);
}

/* font weights */
.fw-3 {
  font-weight: 300;
}

.fw-4 {
  font-weight: 400;
}

.fw-5 {
  font-weight: 500;
}

.fw-6 {
  font-weight: 600;
}

.fw-7 {
  font-weight: 700;
}

.fw-8 {
  font-weight: 800;
}

.fw-9 {
  font-weight: 900;
}

/* font sizes */
.fs-13 {
  font-size: 13px;
}

.fs-14 {
  font-size: 14px;
}

.fs-15 {
  font-size: 15px;
}

.fs-16 {
  font-size: 16px;
}

.fs-17 {
  font-size: 17px;
}

.fs-18 {
  font-size: 18px;
}

.fs-20 {
  font-size: 20px;
}

.fs-22 {
  font-size: 22px;
}

.fs-24 {
  font-size: 24px;
}

.fs-26 {
  font-size: 26px;
}

.fs-60 {
  font-size: 60px;
}

/* letter spacing */
.ls-1 {
  letter-spacing: 1px;
}

.ls-2 {
  letter-spacing: 2px;
}

.flex {
  display: flex;
  align-items: center;
}

.flex-column {
  flex-direction: column;
}

.flex-sb {
  justify-content: space-between;
}

.flex-c {
  justify-content: center;
}

.grid {
  display: grid;
}

/* Button */
.blue-button {
  font-family: inherit;
  font-size: 15px;
  color: var(--white-color);
  background-color: var(--blue-color);
  border: 1px solid var(--blue-color);
  padding: 8px 13px;
  border-radius: 20px;
  margin: 0 10px 10px 0;
  transition: background-color 0.3s;
}

.blue-button:hover {
  background-color: var(--dark-blue-color);
}

.red-button {
  font-family: inherit;
  font-size: 15px;
  color: var(--red-color);
  background-color: var(--white-color);
  border: 1px solid var(--red-color);
  padding: 8px 13px;
  border-radius: 20px;
  margin: 0 10px 10px 0;
  transition: background-color 0.3s;
}

.red-button:hover {
  background-color: var(--dark-red-color);
  color: var(--white-color);
}

.green-button {
  font-family: inherit;
  font-size: 15px;
  color: var(--white-color);
  background-color: #53cf68;
  border: 1px solid #53cf68;
  padding: 8px 13px;
  border-radius: 20px;
  margin: 0 10px 10px 0;
  transition: background-color 0.3s;
}

.green-button:hover {
  background-color: #38a049;
  color: var(--white-color);
}

.invisible-btn {
  background: transparent;
  border: none;
  cursor: pointer;
  font-family: inherit;
}

.form-option {
  border: solid 1px var(--grey-color);
  border-radius: 30px;
  padding: 8px 13px 8px 13px;
  margin: 0 0 10px 10px;
  font-size: 16px;
  font-family: inherit;
  color: var(--dark-grey-color);
}

.added-options {
  margin-top: 10px;
  display: flex;
  flex-wrap: wrap;
}

/* กล่องใส่ search กับ filter */

.search-and-filter {
  display: flex;
  justify-content: space-between; /* จัดให้ห่างกัน */
  align-items: center; /* จัดให้แนวตั้งตรงกลาง */
  gap: 20px; /* ระยะห่างระหว่าง search-bar และ filter */
}

.search-bar {
  width: 400px; /* ให้ search-bar ใช้พื้นที่ที่เหลือ */
}

.filter-bubble-container {
  display: flex;
  gap: 10px;
  margin-bottom: 16px;
}

.filter-bubble {
  font-family: inherit;
  font-size: 14px;
  padding: 8px 16px;
  border-radius: 20px;
  border: 1px solid #ddd;
  background-color: #f0f0f0;
  color: var(--dark-grey-color);
  cursor: pointer;
  transition: background-color 1s, color 0.2s;
}

.filter-bubble:hover {
  background-color: var(--light-grey-color);
}

.filter-bubble.active {
  background-color: var(--blue-color);
  color: var(--white-color);
  border-color: none;
}
