import React from 'react';
import { FaRegTrashAlt } from 'react-icons/fa';
import { DataGrid } from '@mui/x-data-grid';

const IngredientTable = ({ data = [], onEdit, handleClickConfirm }) => {

    const columns = [
        { field: 'name', headerName: 'วัตถุดิบ', width: 200 },
        { field: 'group', headerName: 'หมวดหมู่', width: 150 },
        { field: 'remain', headerName: 'คงเหลือ', type: 'number', width: 140 },
        { field: 'min', headerName: 'ขั้นต่ำ', type: 'number', width: 140 },
        { field: 'unit', headerName: 'หน่วย', type: 'number', width: 80 },
        {
            field: 'actions', headerName: '', type: 'number', width: 150, sortable: false,
            renderCell: (params) => (
                <>
                    <button className="edit-btn" onClick={() => onEdit(params.row)}>✏️</button>
                    <button className="edit-btn text-red" onClick={() => handleClickConfirm(params.row)}>
                        <FaRegTrashAlt />
                    </button>
                </>
            )
        }
    ];

    return (
        <div style={{ height: '90%', width: '100%' }}>
            <DataGrid
                getRowId={(row) => row._id}     //เชื่อมกับ id ใน database ของเรา แต่ id ใน database เป็น _id
                rows={data}
                columns={columns}
                pageSize={5}
                rowsPerPageOptions={[5, 10, 20]}
                sx={{ fontSize: 16, fontFamily: 'inherit' }}
                disableSelectionOnClick
                disableColumnFilter
            />
        </div>
    );
};

// โค้ดตารางแบบเก่า กรณีจะใช้งาน
