import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';
import { HiKey, HiUser } from "react-icons/hi";
import axios from 'axios';
import cafe_bg from '../../images/cafe_bg.jpg';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isAdminMode, setIsAdminMode] = useState(true); // Toggle between admin and normal user modes
    const navigate = useNavigate();
    
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            // ‡πÄ‡∏ï‡∏£‡∏µ‡∏¢‡∏°‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏™‡πà‡∏á‡πÑ‡∏õ‡∏¢‡∏±‡∏á API
            const body = {
                username: isAdminMode ? username : 'employee',
                password
            };
    
            const res = await axios.post('http://localhost:5000/api/users/login', body);
    
            console.log('Response Data:', res.data);

            if (res.data.role_id === '1') {
                navigate('/UserManager');
            } 
            if (res.data.role_id === '2') {
                navigate('/OrderCreate');
            }
    
        } catch (error) {
            console.error('Login Error:', error);
    
            if (error.response) {
                const { status, data } = error.response;
                if (status === 401) {
                    setError('‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏´‡∏£‡∏∑‡∏≠‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
                } else {
                    setError(data.message || '‡πÄ‡∏Å‡∏¥‡∏î‡∏Ç‡πâ‡∏≠‡∏ú‡∏¥‡∏î‡∏û‡∏•‡∏≤‡∏î‡πÉ‡∏ô‡∏Å‡∏≤‡∏£‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö');
                }
            } else {
                setError('‡πÑ‡∏°‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏ï‡πà‡∏≠‡∏Å‡∏±‡∏ö‡πÄ‡∏ã‡∏¥‡∏£‡πå‡∏ü‡πÄ‡∏ß‡∏≠‡∏£‡πå');
            }
        }
    };
    

    return (
        <div className="login-container">
            <div className='fs-20 fw-5' style={{marginBottom: '10px'}}>‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö</div>
            <div className="toggle-mode">
                <div
                    className={`filter-bubble ${isAdminMode ? 'active' : ''}`}
                    onClick={() => setIsAdminMode(true)}
                >
                    <HiKey /> ‡∏ú‡∏π‡πâ‡∏î‡∏π‡πÅ‡∏•‡∏£‡∏∞‡∏ö‡∏ö
                </div>
                <div
                    className={`filter-bubble ${!isAdminMode ? 'active' : ''}`}
                    onClick={() => setIsAdminMode(false)}
                >
                    ‡∏û‡∏ô‡∏±‡∏Å‡∏á‡∏≤‡∏ô <HiUser />
                </div>
            </div>
            <form onSubmit={handleLogin} className="login-form" >
                {isAdminMode ? (
                    <>
                        <input
                            type="text"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                        <input
                            type="password"
                            placeholder="‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </>
                ) : (
                    <input
                        type="password"
                        placeholder="‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å PIN 6 ‡∏´‡∏•‡∏±‡∏Å"
                        maxLength="6"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                )}
                {error && <div className="error-message">{error}</div>}
                
            </form>
            <div className='blue-button' style={{marginTop: '20px'}}onClick={handleLogin}>‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô</div>
        </div>
    );
};

export default Login;
