commits.content:
.menu-list {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
}

/* เพิ่มเมนูใหม่ */
.add-menu-list {
    width: 200px;
    height: 180px;
    background-color: #B2B2B2;
    border: 1px solid #B2B2B2;
    padding: 14px;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 1s, border-color 1s;
    display: flex;  /* Enable Flexbox */
    flex-direction: column;  /* Align items in a column */
    justify-content: center;  /* Center items vertically */
    align-items: center;  /* Center items horizontally */
}

.add-menu-list.selected {
    background-color: var(--blue-color);
}

/* กล่องแสดงเมนูแต่ละอัน */
.menu-item {            
    width: 200px;
    height: 100%;
    border: 1px solid #ddd;
    padding: 10px;
    border-radius: 5px;
    cursor: pointer;
    background-color: var(--white-color);
    box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.1);
    transition: background-color 1s, border-color 1s;
}

.menu-item.selected {
    border: 1px solid var(--light-blue-color-border);
    background-color: var(--light-blue-color);
}

.menu-item img {
    width: 100%;
    height: 100px;
    margin-bottom: 6px;
    border-radius: 5px;
}

/* กล่องขวามือ (กล่องแสดง Detail ของ Menu ที่เราจิ้มเข้าไปดู */

.menu-detail-container {
    display: flex;
    flex-direction: column;
    height: 100%; /* กำหนดให้เต็ม parent */
}

.menu-detail-content {
    flex-grow: 1; /* ให้ส่วนนี้ขยายเต็มที่และ scroll ได้ */
    overflow-y: auto;
    padding: 10px;
}

/* ปุ่มอยู่ด้านล่างและไม่เลื่อน */
.menu-detail-actions {
    flex-shrink: 0; /* ไม่ให้หดตัว */
    padding: 10px;
    background-color: white;
    border-top: 1px solid #ddd;
    display: flex;
    justify-content: space-between;
}

.size-button {
    font-family: inherit;
    font-size: 15px;
    background-color: var(--white-color);
    border: 1px solid #ddd;
    padding: 8px 13px;
    border-radius: 20px;
    margin: 0 10px 10px 0;
    cursor: pointer;
}

.ingredient-ul {
    list-style-type: disc;
    padding-left: 24px;
}


/* จัดเรียงปุ่มทางกล่องขวามือให้อยู่ด้าน */

.menu-action-buttons {
    display: flex;
    justify-content: space-between;
    position: absolute;
    /* สามตัวล่างนี้เป็นเหมือนการบอกให้น้องอยู่ในจุดที่ห่างจาก ล่าง ซ้าย ขวา เท่าไร (ก็คือเรากำหนดตาม padding ที่อยู่ใน .menu-details ได้เลย :D ) */
    bottom: 20px;
    left: 20px;
    right: 20px;
}

.category-filter {
    display: flex;
    gap: 10px;
    margin-bottom: 10px;
}

.category-button {
    padding: 8px 15px;
    border: 1px solid #ccc;
    background-color: #fff;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

.category-button.active {
    background-color: #007bff;
    color: white;
}
