+            .catch(err => console.error('Error fetching :', err));
+            
+        axios.get('http://localhost:5000/api/add_menus/category')
+            .then(res => { 
+                setCategory(res.data) 
+            })
+            .catch(err => console.error('Error fetching :', err));
+        setSaveState(false)
