+
+    // API: 1.Edit 2.Add
+    const handleSaveData = (updatedData) => {
+        if (isEditing) {
+            axios.put(`http://localhost:5000/api/foods/${dataToEdit._id}`, updatedData)
+                .then(res => {
+                    //console.log('แก้ไขรายการอาหารเป็น: ', res.data);
+                    setData(data.map(user => (user._id === res.data._id ? res.data : user)));
+                })
