+                updateOrder(selectedOrder._id, newStatus);
+                Swal.fire({
+                    title: "สำเร็จ!",
+                    text: `ทำการเปลี่ยนสถานะเป็น "${newStatus}" แล้ว`,
+                    icon: "success",
+                    confirmButtonColor: "#64A2FF",
+                    confirmButtonText: "โอเค"
+                });
+            })
+            .catch(err => console.error('Error updating order status:', err));
+    };
+
