+        const hasPhoneNumber = contactInfo.phoneNumber && contactInfo.phoneNumber.trim() !== "";
+        const hasTableNumber = contactInfo.tableNumber && contactInfo.tableNumber.trim() !== "";
+
+        if (!hasPhoneNumber && !hasTableNumber) {
+            Swal.fire({
+                icon: "warning",
